import requestsimport jsonimport csvimport sysfrom colorclass import Color, Windowsfrom terminaltables import AsciiTableclass Arbitr:    def __init__(self, csv_file_name):        self.csv_file_name = csv_file_name        self.URL_BITFINEX = 'https://api.bitfinex.com/v2/tickers?symbols='        self.URL_BITTREX = 'https://bittrex.com/api/v1.1/public/getmarketsummaries'        self.URL_KRAKEN = 'https://api.kraken.com/0/public/Ticker?pair='        self.URL_BINANCE = 'https://api.binance.com/api/v1/ticker/price'        self.URL_EXMO = 'https://api.exmo.com/v1/ticker/'        self.URL_HITBTC = 'https://api.hitbtc.com/api/2/public/ticker/'        self.URL_POLONIEX = 'https://poloniex.com/public?command=returnTicker'        self.names_of_market, self.universal_pairs = self.get_pair_list()    def dell_none_from_list(self, input_list):        return ','.join(item for item in input_list if item != 'None')    def dell_none(self, input_list):        return [item for item in input_list if str(item) != 'None']    def get_all_pairs_by_market(self, market_name):        market_name_chaked = market_name.capitalize()        all_pairs_list = []        with open(self.csv_file_name) as csv_file:            dict_reader = csv.DictReader(csv_file, delimiter=";")            try:                for item in dict_reader:                    all_pairs_list.append(item[market_name_chaked])            except Exception:                return 'Ошибка в названии биржи: ' + str(sys.exc_info()[1])        return all_pairs_list    def get_pair_list(self):         with open(self.csv_file_name, newline='') as csvfile:            csvreader = csv.reader(csvfile)            names_of_markets = csvreader.__next__()[0].split(';')            universal_pairs = dict()            for row in csvreader:                row_list = row[0].split(';')                universal_pairs[row_list[0]] = row_list[1:]            return names_of_markets[1:], universal_pairs    def get_bitfinex_prices(self, pairs):        pair_dict = {}        try:            res = requests.request('GET', self.URL_BITFINEX + self.dell_none_from_list(pairs))            response_list = json.loads(res.text)            for i in range(len(pairs)):                if pairs[i] == 'None':                    response_list.insert(i, 'None')            for pair, key in zip(response_list, self.universal_pairs.keys()):                try:                    pair_dict[key] = float(pair[-4])                except Exception:                    pair_dict[key] = 'None'            return pair_dict        except Exception:            for key in self.universal_pairs.keys():                pair_dict[key] = 'None'            return pair_dict    def get_bittrex_prices(self, pairs):        pair_dict = {}        res = requests.request('GET', self.URL_BITTREX)        response_dict = json.loads(res.text)        for pair, key in zip(pairs, self.universal_pairs.keys()):            if pair == 'None':                pair_dict[key] = 'None'                continue            for item in response_dict['result']:                if item['MarketName'] == pair:                    pair_dict[key] = float(item['Last'])        return pair_dict    def get_binance_prices(self, pairs):        pair_dict = {}        res = requests.request('GET', self.URL_BINANCE)        response_dict = json.loads(res.text)        for pair, key in zip(pairs, self.universal_pairs.keys()):            if pair == 'None':                pair_dict[key] = 'None'                continue            for item in response_dict:                if item['symbol'] == pair:                    pair_dict[key] = float(item['price'])        return pair_dict    def get_kraken_prices(self, pairs):        pair_dict = {}        res = requests.request('GET', self.URL_KRAKEN + self.dell_none_from_list(pairs))        response_dict = json.loads(res.text)        for pair, key in zip(pairs, self.universal_pairs.keys()):            if pair == 'None':                pair_dict[key] = None                continue            try:                kraken_prefix = 'X' + pair[:3] + 'X' + pair[3:]                pair_dict[key] = float(response_dict['result'][kraken_prefix]['c'][0])            except Exception:                pair_dict[key] = None        return pair_dict    def get_exmo_prices(self, pairs):        pair_dict = {}        res = requests.request('GET', self.URL_EXMO)        response_dict = json.loads(res.text)        for pair, key in zip(pairs, self.universal_pairs.keys()):            if pair == 'None':                pair_dict[key] = None                continue            try:                pair_dict[key] = float(response_dict[pair]['last_trade'])            except Exception:                pair_dict[key] = None        return pair_dict    def get_hitbtc_prices(self, pairs):        pair_dict = {}        res = requests.request('GET', self.URL_HITBTC)        response_dict = json.loads(res.text)        for pair, key in zip(pairs, self.universal_pairs.keys()):            if pair == 'None':                pair_dict[key] = None                continue            for item in response_dict:                if item['symbol'] == pair:                    try:                        pair_dict[key] = float(item['last'])                    except Exception:                        pair_dict[key] = None        return pair_dict    def get_poloniex_prices(self, pairs):        pair_dict = {}        try:            res = requests.request('GET', self.URL_POLONIEX)            response_dict = json.loads(res.text)            for pair, key in zip(pairs, self.universal_pairs.keys()):                if pair == 'None':                    pair_dict[key] = 'None'                    continue                pair_dict[key] = float(response_dict[pair]['last'])            return pair_dict        except Exception:            for key in self.universal_pairs.keys():                pair_dict[key] = 'None'            return pair_dict    def get_all_pairs_prices(self):        pairs_prices_dict = {}        bitfinex_prices = self.get_bitfinex_prices(self.get_all_pairs_by_market('Bitfinex'))        bittrex_prices = self.get_bittrex_prices(self.get_all_pairs_by_market('Bittrex'))        binance_prices = self.get_binance_prices(self.get_all_pairs_by_market('Binance'))        kraken_prices = self.get_kraken_prices(self.get_all_pairs_by_market('Kraken'))        exmo_prices = self.get_exmo_prices(self.get_all_pairs_by_market('Exmo'))        hitbtc_prices = self.get_hitbtc_prices(self.get_all_pairs_by_market('Hitbtc'))        poloniex_prices = self.get_poloniex_prices(self.get_all_pairs_by_market('Poloniex'))        for pair in self.universal_pairs.keys():            pairs_prices_dict[pair] = [                bitfinex_prices[pair],                bittrex_prices[pair],                kraken_prices[pair],                binance_prices[pair],                exmo_prices[pair],                hitbtc_prices[pair],                poloniex_prices[pair]            ]        return pairs_prices_dict    def get_min_in_list(self, price_list):        min = 10000000        index = 0        for i in range(len(price_list)):            try:                price_list[i] = float(price_list[i])            except Exception:                continue            if price_list[i] < min:                min = price_list[i]                index = i        name_of_min_market = self.names_of_market[index]        return (min, name_of_min_market)    def get_max_in_list(self, price_list):        max = 0        index = 0        for i in range(len(price_list)):            try:                price_list[i] = float(price_list[i])            except Exception:                continue            if price_list[i] > max:                max = price_list[i]                index = i        name_of_min_market = self.names_of_market[index]        return (max, name_of_min_market)    def print_all_defferences(self):        pairs_prices_dict = self.get_all_pairs_prices()        res_s = ''        for pair in pairs_prices_dict:            res_s += str(pair) + ': ' + str(pairs_prices_dict[pair]) + '    '            min_list = self.get_min_in_list(pairs_prices_dict[pair])            max_list = self.get_max_in_list(pairs_prices_dict[pair])            difference = round((max_list[0] - min_list[0]) * 100 / min_list[0], 2)            res_s += str(difference) + '%  ' + min_list[1] + ' --> ' + max_list[1] + '\n'        print(res_s)    def print_nice_variants(self):        pairs_prices_dict = self.get_all_pairs_prices()        for pair_name, pair_prices_list in pairs_prices_dict.items():            block1 = ''            block2 = ''            pair_prices_list_second_max = pair_prices_list.copy()            max_in_list = self.get_max_in_list(pair_prices_list)            # Тут удаляем 3 минимальных значения            for i in range(len(self.dell_none(pair_prices_list)) - 1):                min_in_list = self.get_min_in_list(pair_prices_list)                print(min_in_list, max_in_list)                difference = round((max_in_list[0] - min_in_list[0]) * 100 / min_in_list[0], 2)                if difference >= 1.5 and difference <= 2:                    block1 += Color('{autogreen}' + '{} --> {}: {}%'.format(min_in_list[1], max_in_list[1], difference)                                    + '{/autogreen}\n')                elif difference > 2:                    block1 += Color('{autoyellow}' + '{} --> {}: {}%'.format(min_in_list[1], max_in_list[1], difference)                                    + '{/autoyellow}\n')                else:                    block1 += '{} --> {}: {}%\n'.format(min_in_list[1], max_in_list[1], difference)                pair_prices_list.remove(min_in_list[0])            # Тут удаляем масимальное значение            pair_prices_list_second_max.remove(max_in_list[0])            max_in_list = self.get_max_in_list(pair_prices_list_second_max)            for i in range(len(self.dell_none(pair_prices_list_second_max)) - 1):                min_in_list = self.get_min_in_list(pair_prices_list_second_max)                difference = round((max_in_list[0] - min_in_list[0]) * 100 / min_in_list[0], 2)                if difference >= 1.5 and difference <= 2:                    block2 += Color('{autogreen}' + '{} --> {}: {}%'.format(min_in_list[1], max_in_list[1], difference)                                    + '{/autogreen}\n')                elif difference > 2:                    block2 += Color('{autoyellow}' + '{} --> {}: {}%'.format(min_in_list[1], max_in_list[1], difference)                                    + '{/autoyellow}\n')                else:                    block2 += '{} --> {}: {}%\n'.format(min_in_list[1], max_in_list[1], difference)                pair_prices_list_second_max.remove(min_in_list[0])            Windows.enable(auto_colors=True, reset_atexit=True)            table_data = [                    [block1, block2]]            table_instance = AsciiTable(table_data, Color('{autored}' + pair_name + '{/autored}'))            table_instance.inner_heading_row_border = False            print(table_instance.table)            print()